import React from "react";
import { propTypes } from "react-bootstrap/esm/Image";
import PropTypes from "prop-types";

const Rating = ({ value, text }) => {
  return (
    <>
      <div>
        <span>
          <i
            className={
              value >= 1
                ? "fas fa-star"
                : value >= 0.5
                ? "fa fa-star-half-alt"
                : "far fa-star"
            }
          />
        </span>
        <span>
          <i
            className={
              value >= 2
                ? "fas fa-star"
                : value >= 1.5
                ? "fa fa-star-half-alt"
                : "far fa-star"
            }
          />
        </span>
        <span>
          <i
            className={
              value >= 3
                ? "fas fa-star"
                : value >= 2.5
                ? "fa fa-star-half-alt"
                : "far fa-star"
            }
          />
        </span>
        <span>
          <i
            className={
              value >= 4
                ? "fas fa-star"
                : value >= 3.5
                ? "fa fa-star-half-alt"
                : "far fa-star"
            }
          />
        </span>
        <span>
          <i
            className={
              value >= 5
                ? "fas fa-star"
                : value >= 4.5
                ? "fa fa-star-half-alt"
                : "far fa-star"
            }
          />
        </span>
        <span> {text && text}</span>
      </div>
    </>
  );
};

// propTypes is  object that is used to define
// the expected type of the props that will be passed to the Rating component.
//there is two props type and value so value must be number and text must be string
Rating.propTypes = {
  value: propTypes.number.isRequired,
  text: propTypes.string.isRequired,
};
export default Rating;
