import React, { useEffect, useState } from "react";
import { Container, Col, Row } from "react-bootstrap";

import Product from "../Components/Product";
import axios from "axios";

const HomeScreen = () => {
  // setproduct set new value of products variable when its update
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await axios.get("/api/products");
      setProducts(data);
    };
    fetchProducts();
  }, []);

  return (
    <>
      <Container>
        <h1>Latest products</h1>
        <Row>
          {/* map function that iterates over an array products and returns a new
        array. In this case, it is used to iterate over each product object in
        the products array. */}
          {products.map((item) => (
            <Col key={item._id} sm={12} md={6} lg={4} xl={3}>
              <Product data={item} />
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
};

export default HomeScreen;
